import './App.css';

import React, { Component } from 'react';
import { render } from 'react-dom';

class Calendar extends Component {
  constructor(props){
    super(props);
    this.state = {
      'loading': true
    };
    this.url = 'http://slack-server-production.us-west-2.elasticbeanstalk.com/calendar/NY/28';
    this.calData = [];
  }

  async componentDidMount(){
    //fetch calendar data
    const response =  await fetch(this.url)
    const data = await response.json();
    for(const key in data) {
      this.calData.push(data[key]);
      // console.log(data[key]);
    };
    this.setState({'loading': false});
    //console.log(this.calData[0]);
    //console.log(this.state);
  }

  render() {
    return (
      <div>
        <h1>Calendar</h1>
        {!this.state.loading ? <Day eventArray={this.calData[0]}/> : 'loading...'}
      </div>
    );
  }
}

function Day(props){
  const dayEvent = [];
  props.eventArray.forEach(element => {
    dayEvent.push(<Event title={element.summary} description={element.description} time={element.start} />)
  });
  return (
    <div className='day'>
    {dayEvent}
    </div>
  )
}

function Event(props){

  function parseDateString(date){
    return date['dateTime'].substring(11, 16).concat(date['dateTime'].substring(19));
  }

  return (
    <div className="eventContainer"><h3>{props.title} {parseDateString(props.time)}</h3> {props.description}</div>
  )
}

render(<Calendar />, document.querySelector('#root'));


export default Calendar;
