import './App.css';

import React, { Component } from 'react';
import { render } from 'react-dom';

class Calendar extends Component {
  constructor(props){
    super(props);
    this.state = {
      'loading': true
    };
    this.url = 'http://slack-server-production.us-west-2.elasticbeanstalk.com/calendar/NY/28';
    this.calData = [];
  }

  async componentDidMount(){
    //fetch calendar data
    const response =  await fetch(this.url)
    const data = await response.json();
    const preData = [];
    for(const key in data) {
      preData.push(data[key]);
      // console.log(data[key]);
    };
    for (let index = 1; index < preData.length; index++) {
      this.calData.push(preData[index]);
    }
    this.setState({'loading': false});
    //console.log(this.calData[0]);
    //console.log(this.state);
  }

  render() {
    const days = [];
    if(!this.state.loading){
      for(let i = 0; i < 7; i++){
        days.push(<Day key={i} eventArray={this.calData[i]}/>)
      }
      return (
        <div className="fuckThis">
          <h1>Calendar - Week of {}</h1>
          <div className='calendar'>

            {days}
          </div>
        </div>
      );
    }
    else{
      return 'loading...';
    }
  }
}

function Day(props){
  const dayEvent = [];

  function parseDateString(date){
    return date['dateTime'].substring(11, 16);
  }

  props.eventArray.forEach((element, index) => {
  dayEvent.push(<Event key={element.start} title={element.summary} time={()=>parseDateString(element.start)}/>)
  });
  return (
    <div className='day'>
    {dayEvent}
    </div>
  )
}

function Event(props){



  return (
    <div className="eventContainer"><h3>{(props.time)} {props.title} </h3></div>
  )
}

render(<Calendar />, document.querySelector('#root'));


export default Calendar;
